mapper.select:
select * from orders

    number=#{number}
    and status=#{status}
    and phone like concat('%', #{phone}, '%')
     and #{endTime} > order_time
     and #{beginTime} < order_time

select sum(amount) from orders

    order_time > #{begin}
     and order_time < #{end}
    and status=#{status}

select count(id) from orders

    order_time > #{begin}
     and order_time < #{end}
    and status=#{status}
