mapper.select:
select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}

select s.id, s.category_id, s.name, s.price, s.status, s.description, s.image, s.update_time, c.name as categoryName
from setmeal s  left join category c on s.category_id = c.id


        s.name like concat('%', #{name}, '%')


        and category_id = #{categoryId}


        and status = #{status}

mapper.update:
update setmeal
category_id=#{categoryId}, name=#{name}, price=#{price}, status=#{status}, description=#{description}, image=#{image}, update_time=#{updateTime}, update_user=#{updateUser},
where id=#{id}

mapper.select:
select count(id) from setmeal


        and status = #{status}


        and category_id = #{categoryId}
